document.addEventListener("DOMContentLoaded", () => {
  var titleAnimation = anime.timeline({
    autoplay: true,
    easing: 'easeOutExpo',
    complete: function() {
      // Callback opcional que é chamado quando a animação é concluída
      console.log("Animação concluída!");
    }
  })
  .add({
    targets: '.animateMe',
    opacity: [0, 1],
    translateY: [-100, 0],
    duration: 3 * 1000, // Convertendo segundos para milissegundos
    delay: anime.stagger(700), // Convertendo para milissegundos
    easing: 'easeOutExpo'
  });
});

